        foreach (var data in articleData)
        {
            var category = categories.First(c => c.Name == data.Category);

            // Логическая привязка тегов
            var matchedTags = data.Category switch
            {
                "Бытовая техника" => tags.Where(t => new[] { "Технологии", "Энергосбережение", "Проверено" }.Contains(t.Name)),
                "Огород и растения" => tags.Where(t => new[] { "Экологично", "Практично", "Советы" }.Contains(t.Name)),
                "Уход за домом" => tags.Where(t => new[] { "Хранение", "Безопасность", "Секреты" }.Contains(t.Name)),
                "Ремонт и инструменты" => tags.Where(t => new[] { "DIY", "Ручной труд", "Бюджетно" }.Contains(t.Name)),
                "Отдых и уют" => tags.Where(t => new[] { "Комфорт", "Декор", "Минимализм" }.Contains(t.Name)),
                "Развлечение" => tags.Where(t => new[] { "Юмор" }.Contains(t.Name)),
                _ => Enumerable.Empty<Tag>()
            };

            var selectedTags = matchedTags
                .OrderBy(_ => _random.Next())
                .Take(_random.Next(0, 3))
                .ToHashSet();

            var article = new Article
            {
                Name = data.Title,
                Description = data.Description,
                CategoryId = category.Id,
                Tags = selectedTags,
                CreatedAt = now,
                CreatedBy = autoSeeding
            };
            articles.Add(article);
